// Custom Script for Doctype: Purchase Invoice
frappe.ui.form.on('Purchase Invoice', {
    onload: function(frm) {
        handle_task_project_linking(frm);
    },
    
    task: function(frm) {
        if (frm.doc.task) {
            frappe.db.get_value('Task', frm.doc.task, 'project', (r) => {
                if (r && r.project) {
                    frm.set_value('project', r.project);
                    
                } else {
                    frm.set_value('project', "");
                    
                }
            });
            copy_task_to_all_rows(frm, frm.doc.task);
        } 
        
    },
    project: function(frm) {
        if (frm.doc.project) {
            if (!frm.doc.task) copy_project_to_all_rows(frm, frm.doc.project);
        } 
        
    }
   
});

function handle_task_project_linking(frm) {
    var task_id = frm.doc.task;
    var current_project_id = frm.doc.project ;
        if (frm.doc.task) {
            frappe.db.get_value('Task', frm.doc.task, 'project', (r) => {
                if (r && r.project) {
                    frm.set_value('project', r.project);
                    
                } else {
                    frm.set_value('project', "");
                    
                }
            });
            copy_task_to_all_rows(frm, frm.doc.task);
        } 
        else {
            if (!current_project_id) return;
            copy_project_to_all_rows(frm, current_project_id);
        }
}

function copy_task_to_all_rows(frm, task_id) {
    let changed = false;
    
    if (frm.doc.items) { 
        frm.doc.items.forEach(function(item) {
            if (!item.task) {
                item.task = task_id; 
                changed = true;
            }
        });
        
        if (changed) {
            frm.refresh_field('items'); 
        }
    }
}

function copy_project_to_all_rows(frm, project_id) {
    let changed = false;
    
    if (frm.doc.items) { 
        frm.doc.items.forEach(function(item) {
            if (!item.project) {
                item.project = project_id; 
                changed = true;
            }
        });
        
        if (changed) {
            frm.refresh_field('items'); 
        }
    }
}

