function handle_task_project_linking(frm) {
    var task_id = frm.doc.task || "";

    if (!task_id){
        if (frm.doc.project) {
            copy_project_to_all_rows(frm, frm.doc.project);
        }
        return; 
    }

    if (!frm.doc.project) {
         frappe.db.get_value('Task', task_id, 'project', function(r) {
            if (r.project) {
